mapper.insert:

	
			select spring_board_seq.nextval from dual
	
	INSERT INTO spring_board(
					  b_num
					, b_name
					, b_title
					, b_content
					, b_pwd
				)
		VALUES(#{b_num}
				  , #{b_name}
				  , #{b_title}
				  , #{b_content}
				  , #{b_pwd})

mapper.select:
select nvl((      
	select 1 from spring_board
	where b_num = #{b_num}
	and b_pwd = #{b_pwd}
	), 0)as state
	
	from dual

mapper.update:
update spring_board
set 
	b_title = #{b_title}
  , b_name = #{b_name}
  , b_content = #{b_content}
  , b_date = sysdate
  
  , b_pwd = #{b_pwd}
  	
where b_num = #{b_num}
